import actionTypes from '../actionTypes'

const initialState = {
    userInfo: {}
}

const countryList = (data) => {
    var lookup = {};
    var items = data;
    var result = [];

    for (var item, i = 0; item = items[i++];) {
        var country = item.address.country;

        if (!(country in lookup)) {
            lookup[country] = 1;
            result.push(country);
        }
    }
    return result;
}

const cityList = (data) => {
    var lookup = {};
    var items = data;
    var result = [];

    for (var item, i = 0; item = items[i++];) {
        var city = item.address.city;

        if (!(city in lookup)) {
            lookup[city] = 1;
            result.push(city);
        }
    }
    return result;
}

const dataReducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.FEATCH_USER_OK:
            return { ...state, userInfo: action.user };
        case actionTypes.FEATCH_DATA_OK:
            let countryList = countryList(action.data)
            let cityList = cityList(action.data)
            return { ...state, data: action.data, countries: countryList, cities: cityList };
        case actionTypes.CLICK_COUNTRY:
            let filters = state.filters;
            let countries = filters.countries;
            let country = action.value;
            let pos = countries.indexOf(country);
            if (pos < 0) {
                countries.push(country)
            } else {
                countries.splice(pos, 1)
            }
            filters = { ...filters, countries: countries }
            return { ...state, filters: filters };

        case actionTypes.CLICK_CITY:
            filters = state.filters;
            let cities = filters.cities;
            let city = action.value;
            pos = cities.indexOf(city);
            if (pos < 0) {
                cities.push(city)
            } else {
                cities.splice(pos, 1)
            }
            filters = { ...filters, cities: cities }
            return { ...state, filters: filters };
        default:
            return state;
    }
}

export default dataReducer;
