import React from 'react';

class CountryFilter extends React.Component {
    render() {
        let { countryList, onClickCountry, selected} = this.props;

        let countries = null;
        if (!!countryList) {
            countries = countryList.map((country) => (
                <li onClick={() => onClickCountry(country)}>
                    <input id={country} type="checkbox" name={country} checked={selected.indexOf(country)?true:false}/>
                    <label htmlFor={country}>{country}</label>
                </li>
            ));
        }
        return (
            <div>
                <h3>
                    COUNTRY
                </h3>
                <ul>
                    {countries}
                </ul>
            </div>
        );
    }
}

class CityFilter extends React.Component {
    render() {
        let { cityList, onClickCity, selected } = this.props;

        let cities = null;
        if (!!cityList) {
            cities = cityList.map((city) => (
                <li onClick={() => onClickCity(city)}>
                    <input id="{city}" type="checkbox" name={city}></input>
                    <lable htmlFor="{city}">{city}</lable>
                </li>
            ));
        }
        return (
            <div>
                <h3>
                    CITY
                </h3>
                <ul>
                    {cities}
                </ul>
            </div>
        );
    }
}

export { CountryFilter, CityFilter }
