import actionTypes from '../actionTypes'

const initialState = {
    employees: [],
    roles: [],
    selectedEmployee: {}
}

const dataReducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.FEATCH_EMPLOYEES_OK:
            return { ...state, employees: action.employees.employees, pageInfo: action.employees.pageInfo };
        case actionTypes.FEATCH_ROLES_OK:
            return { ...state, roles: action.roles };
        case actionTypes.FEATCH_SELECTED_EMPLOYEE_OK:
            return { ...state, selectedEmployee: action.selectedEmployee };
        case actionTypes.FEATCH_EMPLOYEE_TOBE_CHANGE_OK:
            return { ...state, employeeTobeChange: action.employeeTobeChange };
        case actionTypes.FEATCH_EMPLOYEE_FOR_DISPLAY_OK:
            return { ...state, employeeForDisplay: action.employeeForDisplay };
        case actionTypes.CHANGE_FILTER:
            let { filters } = state;

            if (action.value === '') {
                try {
                    delete filters.employeeName
                    return filters;
                } catch (error){
                    console.log(error);
                }
            }

            switch (action.field) {
                case 'employeeName':
                    if (action.value === '') {
                        try {
                            delete filters.employeeName
                        } catch (error){
                            console.log(error);
                        }
                    }
                    filters = { ...filters, employeeName: action.value };
                    break;
                case 'EmployeeNumber':
                    filters = { ...filters, EmployeeNumber: action.value };
                    break;
                case 'DateJoined':
                    filters = { ...filters, DateJoined: action.value };
                    break;
                case 'Extension':
                    filters = { ...filters, Extension: action.value };
                    break;
                case 'RoleName':
                    filters = { ...filters, RoleName: action.value };
                    break;
                default:
                    break;
            }
            return { ...state, filters: filters };
        default:
            return state;
    }
}

export default dataReducer;
