import actionTypes from '../actionTypes'

export function initialLoad() {
  return {
    type: actionTypes.FETCH_EMPLOYEES_ASYNC
  }
}

export function onClickEmployee(value) {
  return {
    type: actionTypes.CLICK_EMPLOYEE_ASYNC,
    value
  }
}

export function onUpdateEmployee(value) {
  return {
    type: actionTypes.UPDATE_EMPLOYEE_ASYNC,
    EmployeeID,
    EmployeeNumber,
    FirstName,
    LastName,
    Extension,
    EmployeeID
  }
}

export function onClickPageNo(pageSize, pageNo) {
  return {
    type: actionTypes.CLICK_PAGENO_ASYNC,
    pageSize,
    pageNo
  }
}

export function onChangePageSize(pageSize, pageNo) {
  return {
    type: actionTypes.CHANGE_PAGE_SIZE_ASYNC,
    pageSize,
    pageNo
  }
}
