import React from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux'
import { onClickCountry, onClickCity } from '../actions'
import { CountryFilter, CityFilter } from './leftShow'

class Left extends React.Component {
    render() {
        let { data, countries, cities } = this.props;
        
        /*
        let showStr = '';
        if (!!data) {
            showStr = data[0].clientName;
        }
        let countryList = ['Australia', 'German']
        */
        return (
            <div align="left">
                <CountryFilter countryList = {countries} />
                <CityFilter cityList = {cities} />
            </div >
        );
    }
}


const mapStateToProps = (state) => {
    let { data, countries, cities } = state
    return { data: data, countries: countries, 
        cities: cities };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClickCountry: bindActionCreators(onClickCountry, dispatch),
        onClickCity: bindActionCreators(onClickCity, dispatch)
    }
}

export default connect(mapStateToProps)(Left)
