import React from 'react'
import { connect } from 'react-redux'

class Main extends React.Component {

    render() {
        let { data, showImage } = this.props;
        let imageUrl = '';

        if(!!data) {
            let pos = data.id.indexOf(showImage);
            imageUrl = data[pos].imageUrl;
        }

        return (
            <div id = "main">
            <img src = {imageUrl} ></img>
            </div>
        );
    }
}


const mapStateToProps = (state) => {
    let { data, showImage } = state.dataReducer
    return { data: data, showImage: showImage };
}

export default connect(mapStateToProps)(Main)
