import React from 'react'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { onClickEmployee } from '../actions'
import actionTypes from '../actionTypes'
import store from '../store'
import { EmployeeLeft } from './employeeLeft'
import { EmployeeDetail } from './employeeDetail'
import querySearch from 'stringquery'



class Employee extends React.Component {
    componentDidMount() {
        store.dispatch({type: actionTypes.FETCH_EMPLOYEES_ASYNC});
    }
    render() {
        let { employees, count, selectedEmployee, onClickEmployee, onClickPageNo } = this.props;

        return (
            <div>
                <div id='left'>
                    <EmployeeLeft employees={employees} count={count} onClickEmployee = {onClickEmployee}
                    onClickPageNo = {onClickPageNo} />
                </div>
                <div id='middle'>
                    <EmployeeDetail selectedEmployee={selectedEmployee} />
                </div>
                <div id='right'>
                    
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    let { employees, count, selectedEmployee } = state.dataReducer
    return {
        employees: employees, count: count, selectedEmployee: selectedEmployee
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClickEmployee: bindActionCreators(onClickEmployee, dispatch),
        onClickPageNo: bindActionCreators(onClickPageNo, dispatch)
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Employee)
