import React from 'react'
import { connect } from 'react-redux'
import {onClickAdd, onClickSubstract} from '../actions'

class Main extends React.Component {
    render() {
        let { value, onClickAdd, onClickSubstract } = this.props;
        return (
            <div>
                <p>{value}</p>
                <button onClick={onClickAdd}>加1</button>
                <button onClick={onClickSubstract}>减1</button>
            </div>
        );
    }
}


const mapStateToProps = (state) => {
    return { value: state.calReducer.value };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClickAdd: bindActionCreators(onClickAdd, dispatch),
        onClickSubstract: bindActionCreators(onClickSubstract, dispatch)
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Main)
