import { put, takeEvery, call } from 'redux-saga/effects'
import actionTypes from '../actionTypes'

/*
export function* clickEmployeeAsync(action) {
    let EmployeeID = action.value;
    try {
        console.log('http://localhost:5000/api/v1/employee/'+EmployeeID);
        const response = yield call(fetch, 'http://localhost:5000/api/v1/employee/'+EmployeeID);
        const employee = yield call([response, 'json']);
        if (!!employee) {
            yield put({ type: actionTypes.FEATCH_SELECTED_EMPLOYEE_OK, selectedEmployee: employee});
        }
        //return data[0].usernameName;
    } catch (e) {
        console.log(e);
        //return null;
    }
}
*/

export function* clickEmployeeAsync(action) {
    let value = action.value;
    console.log(value);
    yield put({ type: actionTypes.FEATCH_SELECTED_EMPLOYEE_OK, value: value })
}

export function* watchClickEmployee() {
    yield takeEvery(actionTypes.CLICK_EMPLOYEES_ASYNC, clickEmployeeAsync);
}
