import React from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux'
import { onClickEmployee } from '../actions'
import { EmployeeDetail } from './employeeDetail'

class Employee extends React.Component {
    render() {
        let { employees, roles, selectedEmployee, onClickEmployee } = this.props;
        return (
            <div>
                <div className='left'>
                    {employeeList}
                </div >
                <div className='right'>
                    <EmployeeLeft employeeList={employeeList} onClickEmployee = {onClickEmployee} />
                </div>
                <div className='right'>
                    <EmployeeDetail selectedEmployee={selectedEmployee} />
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    let { employees, roles, selectedEmployee } = state.dataReducer
    return {
        employees: employees, roles: roles, selectedEmployee: selectedEmployee
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClickEmployee: bindActionCreators(onClickEmployee, dispatch)
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Employee)
