import React from 'react';
import store from '../store'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import actionTypes from '../actionTypes'
import { onUpdateEmployee } from '../actions'

class ChangeEmployee extends React.Component {
    componentDidMount() {
        let pathname = this.props.location.pathname
        let EmployeeID = pathname.substring(pathname.lastIndexOf('/') + 1);
        store.dispatch({ type: actionTypes.FETCH_EMPLOYEE_FOR_DISPLAY_ASYNC, EmployeeID: EmployeeID });
    }
    render() {
        let { employeeForDisplay } = this.props;

        let roleList = '';
        console.log(employeeForDisplay);

        let detailItems = '';
        if (!!employeeForDisplay) {

            detailItems = (
                <table>
                    <tbody>
                        <tr>
                            <td>Employee ID: </td>
                            <td><input type="text" id="EmployeeID" value={employeeTobeChange.EmployeeID}></input></td>
                        </tr>
                        <tr>
                            <td>Employee Number:</td>
                            <td><input id="EmployeeNumber" defaultValue={employeeTobeChange.EmployeeNumber}></input></td>
                        </tr>
                        <tr>
                            <td>First Name:</td>
                            <td><input id="FirstName" defaultValue={employeeTobeChange.FirstName}></input> </td>
                        </tr>
                        <tr>
                            <td>Last Name:</td>
                            <td><input id="LastName" defaultValue={employeeTobeChange.LastName}></input> </td>
                        </tr>
                        <tr>
                            <td>Extension:</td>
                            <td><input id="Extension" min="0" max="655635" defaultValue={employeeTobeChange.Extension}></input></td>
                        </tr>
                        <tr>
                            <td>RoleName:</td>
                            <td><input id="Extension" defaultValue={employeeTobeChange.Extension}></input></td>
                        </tr>
                    </tbody>
                </table>);
        }
        return (
            <div id="employee-detail">
                {detailItems}
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    let { employeeForDisplay } = state.dataReducer
    return {
        employeeForDisplay: employeeForDisplay
    };
}

export default connect(mapStateToProps)(ChangeEmployee)
