import React from 'react';
import store from '../store'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import actionTypes from '../actionTypes'
import { onUpdateEmployee } from '../actions'

class ChangeEmployee extends React.Component {
    componentDidMount() {
        let pathname = this.props.location.pathname
        let EmployeeID = pathname.substring(pathname.lastIndexOf('/') + 1);
        store.dispatch({type: actionTypes.FETCH_EMPLOYEE_TOBE_UPDATE_ASYNC, EmployeeID: EmployeeID});
    }
    render() {
        let { employeeTobeChange } = this.props;

        console.log(JSON.stringify(this.props.location));

        let detailItems = '';
        if (!!employeeTobeChange) {
            detailItems = (
                <table>
                <tr>
                    <td>Employee ID: </td>
                    <td><text>{employeeTobeChange.EmployeeID}</text></td>
                </tr>
                <tr>
                    <td>Employee Number:</td>
                    <td><input value = {employeeTobeChange.EmployeeNumber}></input></td>
                </tr>
                <tr>
                    <td>Employee Name: {employeeTobeChange.FirstName} {employeeTobeChange.LastName} </td>
                </tr>
                <tr>
                    <td>Date Joined: {employeeTobeChange.DateJoined}</td>
                </tr>
                <tr>
                    <td>Extension: {employeeTobeChange.Extension}</td>
                </tr>
                <tr>
                    <td>RoleName: {employeeTobeChange.Role!==null?employeeTobeChange.RoleID:null}</td>
                </tr></table>);
        }
        return (
            <div id="employee-detail">
                    {detailItems}
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    let { employeeTobeChange } = state.dataReducer
    return {
        employeeTobeChange: employeeTobeChange, 
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onUpdateEmployee: bindActionCreators(onUpdateEmployee, dispatch),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ChangeEmployee)
