import actionTypes from '../actionTypes'

const initialState = {
    employees: [],
    roles: []
}

//filter the select records
const multiFilter = (data, filters) => {
    const filterKeys = Object.keys(filters);
    if (filterKeys.length === 0) {
        return data;
    }
    return data.filter((item) => {
        return filterKeys.some(key => { //.every for case need to match two filters, .some only need to match anyone
            //if(!filters[key].length){
            //    return true;
            //} 
            return !!~filters[key].indexOf(item.address[key]);
        })
    })
}

const dataReducer = (state = initialState, action) => {
    let { employees, roles, selectedEmployee } = state;
    switch (action.type) {
        case actionTypes.FEATCH_EMPLOYEES_OK:
            return { ...state, employees: action.employees, selectedEmployee: action.employees[0] };
        case actionTypes.FEATCH_ROLES_OK:
            return { ...state, employees: action.employees };
        case actionTypes.FEATCH_SELECTED_EMPLOYEE_OK:
            selectedEmployee = employees[action.value];
            return { ...state, selectedEmployee: selectedEmployee };
        default:
            return state;
    }
}

export default dataReducer;
