import React from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux'
import { CountryFilter, CityFilter } from './leftShow'
import {onClickCity, onClickCountry} from '../actions'

class Left extends React.Component {
    render() {
        let { data, countries, cities, filters, onClickCountry, onClickCity } = this.props;
        
        /*
        let showStr = '';
        if (!!data) {
            showStr = data[0].clientName;
        }
        let countryList = ['Australia', 'German']
        */

        const changeStr = () => {
            return filters = (new Date()).toLocaleDateString() ;
        }
        return (
            <div align="left">
                <CountryFilter countryList = {countries} onClickCountry = {onClickCountry} />
                <CityFilter cityList = {cities} onClickCity = {onClickCity}  />
                <button onClick = {changeStr}>test parent click</button>
                <p>{filters}</p>
            </div >
        );
    }
}


const mapStateToProps = (state) => {
    let { data, countries, cities, filters } = state.dataReducer
    return { data: data, countries: countries, 
        cities: cities, filters: filters };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClickCountry: bindActionCreators(onClickCountry, dispatch),
        onClickCity: bindActionCreators(onClickCity, dispatch)
    }
}

export default connect(mapStateToProps)(Left)
