import React from 'react';

class EmployeeLeft extends React.Component {
    render() {
        let { employees, pageInfo, onClickEmployee } = this.props;
        let employeeList = null;


        let pageLink = [];
        let pageSize = 5;
        let pageNo = 1;
        let totalPageNo = 1;

        
        if (typeof pageInfo !== 'undefined' && Object.keys(pageInfo).length !== 0) {
            pageNo = pageInfo.pageNo
            pageSize = pageInfo.pageSize
            totalPageNo = Math.ceil(pageInfo.count / pageSize);
        }
        
        if (employees.length !== 0) {
            employeeList = employees.map((employee, rowid) => (
                <tr key={employee.EmployeeID}>
                    <td>{(rowid + 1 + (pageNo - 1) * pageSize)}</td>
                    <td><a href='' onClick={() => onClickEmployee(employee.EmployeeID)}>
                         {employee.FirstName} {employee.LastName}</a>
                    </td>
                    <td>{employee.EmployeeNumber}</td>
                </tr>
            ));
            for (let i = 0; i < totalPageNo; i++) {
                pageLink.push(
                    <td key={i}>
                    <a href={'./employees?pageSize=' + pageSize + '&pageNo=' + (i + 1)}> {i + 1} </a>
                    </td>
                );
            }
            //console.log("totalPageNo:" + totalPageNo);
            //console.log("pageLink:" + pageLink);
        }
        return (
            <div>
                <table>
                    <tbody>
                <thead>
                    <tr key="header">
                        <th>
                            Row No.
                        </th>
                        <th>
                            Employee Name
                        </th>
                        <th>
                            Employee Number
                        </th>
                    </tr>
                </thead>
                {employeeList}
                </tbody>
                </table>
                <table>
                    <tr key="pages">
                        <td>Pages:</td> 
                        {pageLink}
                    </tr>
                </table>
            </div>
        );
    }
}

export { EmployeeLeft }
