import actionTypes from '../actionTypes'

const initialState = {
    userInfo: {}
}

const countryList = (data) => {
    var lookup = {};
    var items = data;
    var result = [];

    for (var item, i = 0; item = items[i++];) {
        var country = item.address.country;

        if (!(country in lookup)) {
            lookup[country] = 1;
            result.push(country);
        }
    }
    return result;
}

const cityList = (data) => {
    var lookup = {};
    var items = data;
    var result = [];

    for (var item, i = 0; item = items[i++];) {
        var city = item.address.city;

        if (!(city in lookup)) {
            lookup[city] = 1;
            result.push(city);
        }
    }
    return result;
}

const dataReducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.FEATCH_USER_OK:
            return { ...state, userInfo: action.user };
        case actionTypes.FEATCH_DATA_OK:
            let countries1 = countryList(action.data)
            let cities1 = cityList(action.data)
            return { ...state, data: action.data, countries: countries1, cities: cities1 };
        case actionTypes.CLICK_COUNTRY:
            let filters1 = state.filters;
           
            let countries = [];
            if (typeof filters1.countries != 'undefined') {
                countries = filters1.countries;
            }
            console.log(countries);
            let country = action.value;
            let pos1 = countries.indexOf(country);
            console.log(country)
            if (pos1 < 0) {
                countries.push(country)
            } else {
                countries.splice(pos1, 1)
            }
            filters1 = { ...filters1, countries: countries }
            return { ...state, filters: filters1 };

        case actionTypes.CLICK_CITY:
            let filters2 = state.filters;
            let cities = [];
            if (typeof filters2.cities != undefined) {
                cities = filters2.cities;
            }
            let city = action.value;
            let pos2 = cities.indexOf(city);
            if (pos2 < 0) {
                cities.push(city)
            } else {
                cities.splice(pos2, 1)
            }
            filters2 = { ...filters2, cities: cities }
            return { ...state, filters: filters2 };
        default:
            return state;
    }
}

export default dataReducer;
