import React from 'react'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { onClickEmployee, onChangePageSize } from '../actions'
import actionTypes from '../actionTypes'
import store from '../store'
import { EmployeesList } from './employeeList'



class Employee extends React.Component {
    componentDidMount() {
        store.dispatch({type: actionTypes.FETCH_EMPLOYEES_ASYNC, query: this.props.location.search});
    }
    render() {
        let { employees, pageInfo, onClickEmployee, onChangePageSize } = this.props;

        //let queryParams = querySearch(this.props.location.search);

        return (
            <div>
                <div id='left'>
                    <EmployeesList employees={employees} pageInfo={pageInfo} onClickEmployee = {onClickEmployee}
                     onChangePageSize = {onChangePageSize} />
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    let { employees, pageInfo } = state.dataReducer
    return {
        employees: employees, pageInfo: pageInfo, 
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClickEmployee: bindActionCreators(onClickEmployee, dispatch),
        onChangePageSize: bindActionCreators(onChangePageSize, dispatch),
        onDeleteEmployee: bindActionCreators(onDeleteEmployee, dispatch),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Employee)
