import React from 'react';

class EmployeeLeft extends React.Component {
    render() {
        let { employees, pageInfo, onClickEmployee, onChangePageSize } = this.props;
        let employeeList = null;


        let pageLink = [];
        let pageSize = 5;
        let pageNo = 1;
        let totalPageNo = 1;


        if (typeof pageInfo !== 'undefined' && Object.keys(pageInfo).length !== 0) {
            pageNo = pageInfo.pageNo
            pageSize = pageInfo.pageSize
            totalPageNo = Math.ceil(pageInfo.count / pageSize);
        }

        if (employees.length !== 0) {
            employeeList = employees.map((employee, rowid) => (
                <tr key={employee.EmployeeID}>
                    <td>{(rowid + 1 + (pageNo - 1) * pageSize)}</td>
                    <td><a href={'./employee/'+employee.EmployeeID} onClick={() => onClickEmployee(employee.EmployeeID)}>
                        {employee.FirstName} {employee.LastName}</a>
                    </td>
                    <td>{employee.EmployeeNumber}</td>
                    <td>{employee.DateJoined.substr(0,10)}</td>
                    <td>{employee.Extension}</td>
                    <td>{Object.keys(employee.Role).length!==0?employee.Role.RoleName:''}</td>
                    <td><button>Change</button></td>
                    <td><button>Delete</button></td>
                </tr>
            ));
            for (let i = 0; i < totalPageNo; i++) {
                pageLink.push(
                    <td key={i}>
                        <a href={'./employees?pageSize=' + pageSize + '&pageNo=' + (i + 1)}> {i + 1} </a>
                    </td>
                );
            }
            //console.log("totalPageNo:" + totalPageNo);
            //console.log("pageLink:" + pageLink);
        }
        return (
            <div>
                <table>
                    <thead>
                        <tr key="header">
                            <th>Row No.</th>
                            <th>Employee Name</th>
                            <th>Employee Number</th>
                            <th>Date Joined</th>
                            <th>Extension</th>
                            <th>Role</th>
                            <th>Change</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                    {employeeList}
                    </tbody>
                </table>
                <table>
                    <tbody>
                    <tr key="pages">
                        <td>Pages:</td>
                        {pageLink}
                        <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>
                        <td>Page Size:</td>
                        <td><input size = "2" type = "text" id="inputPageSize" onChange = {(e) => onChangePageSize(e.target.value, pageNo)}></input></td>
                    </tr>
                    </tbody>
                </table>
            </div>
        );
    }
}

export { EmployeeLeft }
