import { put, takeEvery, delay, all } from 'redux-saga/effects'

export function* substractAsync() {
    yield delay(2000);
    yield put({type: 'CLICK_SUBSTRACT'})
}

export function* watchSubstractAsync() {
    yield takeEvery('CLICK_SUBSTRACT_ASYNC', substractAsync);
}
