import React from 'react';

class EmployeeDetail extends React.Component {
    render() {
        let { selectedEmployee } = this.props;

        let detailItems = '';
        if (!!selectedEmployee) {
            detailItems = (
                <ul>
                    <li>ID: {selectedEmployee.EmployeeID}</li>
                    <li>Employee Number: {selectedEmployee.EmployeeNumber}</li>
                    <li>Employee Name: {selectedEmployee.FirstName} {selectedEmployee.LastName} </li>
                    <li>Date Joined: {selectedEmployee.DateJoined}</li>
                    <li>Extension: {selectedEmployee.Extension}</li>
                    <li>RoleID: {selectedEmployee.RoleID}</li>
                </ul>);
        }
        return (
            <div id="employee-detail">
                {detailItems}
            </div>
        );
    }
}

export { EmployeeDetail }
