import React from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux'
import { CountryFilter, CityFilter } from './leftShow'
import { ImageList } from './imageList'
import {onClickCity, onClickCountry} from '../actions'

class Left extends React.Component {
    render() {
        let { data, countries, cities, filters, filterResult, onClickCountry, onClickCity } = this.props;
        return (
            <div>
                <CountryFilter countryList = {countries} onClickCountry = {onClickCountry} />
                <CityFilter cityList = {cities} onClickCity = {onClickCity}  />
                <ImageList filterResult = {filterResult} />
            </div >
        );
    }
}

const mapStateToProps = (state) => {
    let { data, countries, cities, filters, filterResult } = state.dataReducer
    return { data: data, countries: countries, 
        cities: cities, filters: filters, filterResult: filterResult };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClickCountry: bindActionCreators(onClickCountry, dispatch),
        onClickCity: bindActionCreators(onClickCity, dispatch)
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Left)
