import { createStore, combineReducers, applyMiddleware } from 'redux';

const sagaMiddleware = createSagaMiddleware();
const store = createStore(myReducer, applyMiddleware(sagaMiddleware));
const runSaga = sagaMiddleware.run();

export {
    store,
    runSaga
}
