import React from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux'
import { CountryFilter } from './leftShow'

class Left extends React.Component {
    render() {
        let { data, countries, cities } = this.props;
        
        /*
        let showStr = '';
        if (!!data) {
            showStr = data[0].clientName;
        }
        let countryList = ['Australia', 'German']
        */
        return (
            <div align="left">
                <CountryFilter countryList = {countries} />
            </div >
        );
    }
}


const mapStateToProps = (state) => {
    return { data: state.initLoadReducer.data, countries: state.initLoadReducer.countries, 
        cities: state.initLoadReducer.cities };
}

export default connect(mapStateToProps)(Left)
