import { put, takeEvery, delay} from 'redux-saga/effects'

export function* initLoadAsync() {
    yield delay(2000);
    yield put({ type: 'CLICK_ADD' })
}

function* fetchUser(){
    try {
      const response = yield call(fetch, 'https://gist.githubusercontent.com/Lachlanbsmith/858629f93f628c62c4e29a3bb1d99bff/raw/bc403f2f39fbd3d41e48ff893f24d0545907ece0/login');
      const data = response.json();
      console.log(data); // prints "1"
      return str(data[0]); // explicitly set radix
    } catch (e) {
      console.log(e);
      return null; // good to return something here
    }
  }

export function* watchInitLoad() {
    yield takeEvery('INIT_LOAD_DATA_ASYNC', addAsync);
}
