import React from 'react';
import store from '../store'

class ChangeEmployee extends React.Component {
    render() {
        let { employeeTobeChange } = this.props;

        let detailItems = '';
        if (!!employeeTobeChange) {
            detailItems = (
                <table>
                <tr>
                    <td>ID: {employeeTobeChange.EmployeeID}</td>
                </tr>
                <tr>
                    <td>Employee Number: {employeeTobeChange.EmployeeNumber}</td>
                </tr>
                <tr>
                    <td>Employee Name: {employeeTobeChange.FirstName} {employeeTobeChange.LastName} </td>
                </tr>
                <tr>
                    <td>Date Joined: {employeeTobeChange.DateJoined}</td>
                </tr>
                <tr>
                    <td>Extension: {employeeTobeChange.Extension}</td>
                </tr>
                <tr>
                    <td>RoleName: {employeeTobeChange.Role!==null?employeeTobeChange.RoleID:null}</td>
                </tr></table>);
        }
        return (
            <div id="employee-detail">
                    {detailItems}
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    let { employees, pageInfo } = state.dataReducer
    return {
        employees: employees, pageInfo: pageInfo, 
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClickChangeEmployee: bindActionCreators(onClickEmployee, dispatch),
        onChangePageSize: bindActionCreators(onChangePageSize, dispatch),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ChangeEmployee)
