import {actionTypes} from '../actionTypes'

const initialState = {
  value: 0
}

const addReducer = (state = { value: 0 }, action) => {
    let value = state.value;
    switch (action.type) {
        case "CLICK_ADD":
            return { ...state, value: value + 1 };
        default:
            return state;
    }
}

export default addReducer
