import actionTypes from '../actionTypes'

const initialState = {
    userInfo: {}
}

const countryList = (data) => {
    var lookup = {};
    var items = data;
    var result = [];

    for (var item, i = 0; item = items[i++];) {
        var country = item.address.country;

        if (!(country in lookup)) {
            lookup[country] = 1;
            result.push(country);
        }
    }
    return result;
}

const cityList = (data) => {
    var lookup = {};
    var items = data;
    var result = [];

    for (var item, i = 0; item = items[i++];) {
        var city = item.address.city;

        if (!(city in lookup)) {
            lookup[city] = 1;
            result.push(city);
        }
    }
    return result;
}

const updateFilter = (filter = [], value) => {
    if (typeof (filter) == 'undefined') {
        filter = [];
    }
    let pos = filter.indexOf(value);
    if (pos < 0) {
        filter.push(value)
    } else {
        filter.splice(pos, 1)
    }
    return filter;
}

const dataReducer = (state = initialState, action) => {
    let { filters } = state;
    switch (action.type) {
        case actionTypes.FEATCH_USER_OK:
            return { ...state, userInfo: action.user };
        case actionTypes.FEATCH_DATA_OK:
            let countries1 = countryList(action.data)
            let cities1 = cityList(action.data)
            return { ...state, data: action.data, countries: countries1, cities: cities1 };
        case actionTypes.CLICK_COUNTRY:
            let countries = [];
            try {
                countries = filters.countries;
            }
            catch (e) {
                console.log("no countries object yet");
            }
            countries = updateFilter(countries, action.value);
            filters = { ...filters, countries: countries };
            return { ...state, filters: filters };

        case actionTypes.CLICK_CITY:
            let cities = [];
            try {
                cities = filters.cities;
            }
            catch (e) {
                console.log("no cities object yet");
            }
            cities = updateFilter(cities, action.value);
            filters = { ...filters, cities: cities };
            return { ...state, filters: filters };
        default:
            return state;
    }
}

export default dataReducer;
