import React from 'react';
import { onClickCountry, onClickCity } from '../actions'

class CountryFilter extends React.Component {
    render() {
        let {countryList, onClickCountry} = this.props;

        let countries = null;
        if(!!countryList) {
            countries = countryList.map((country) => (
                <li onClick = {()=>this.onClickCountry(country)}>{country}</li>
        ));
        }
        
        return (
            <div>
                <h3>
                    COUNTRY
                </h3>
                <ul>
                    {countries}
                </ul>
            </div>
        );
    }
}

class CityFilter extends React.Component {
    render() {
        let {cityList, onClickCity} = this.props;

        let cities = null;
        if(!!cityList) {
            cities = cityList.map((city) => (
                <li onClick = {()=>this.onClickCity(city)}>{city}</li>
        ));
        }
        
        return (
            <div>
                <h3>
                    CITY
                </h3>
                <ul>
                    {cities}
                </ul>
            </div>
        );
    }
}

export {CountryFilter, CityFilter}
