import { put, takeEvery, call } from 'redux-saga/effects'
import actionTypes from '../actionTypes'


export function* clickChangeEmployeeAsync(action) {
    let EmployeeID = action.value;
    try {
        //console.log('http://localhost:5000/api/v1/employee/'+EmployeeID);
        const response = yield call(fetch, 'http://localhost:5000/api/v1/employee/'+EmployeeID);
        const employee = yield call([response, 'json']);
        if (!!employee) {
            yield put({ type: actionTypes.FEATCH_EMPLOYEE_TOBE_CHANGE_OK, employeeTobeChange: employee});
        }
        //return data[0].usernameName;
    } catch (e) {
        console.log(e);
        //return null;
    }
}

export function* watchClickChangeEmployee() {
    yield takeEvery(actionTypes.CLICK_CHANGE_EMPLOYEE_ASYNC, clickChangeEmployeeAsync);
}
