import React from 'react'
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux'
import { onClickAdd, onClickSubstract } from '../actions'

class Main extends React.Component {

    onClickAdd() {
        this.props.onClickAdd();
    }
    onClickSubstract() {
        this.props.onClickSubstract();
    }
    render() {
        let { value } = this.props;
        return (
            <div>
                <p>{value}</p>
                <button onClick={() => this.onClickAdd()}>加1</button>
                <button onClick={() => this.onClickSubstract()}>减1</button>
            </div>
        );
    }
}


const mapStateToProps = (state) => {
    return { value: state.calReducer.value };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClickAdd: bindActionCreators(onClickAdd, dispatch),
        onClickSubstract: bindActionCreators(onClickSubstract, dispatch)
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Main)
