import { put, takeEvery, call } from 'redux-saga/effects'
import actionTypes from '../actionTypes'

export function* fetchEmployeeAsync(action) {
    try {
        let queryStr = action.query;

        let whereStr = JSON.stringify(action.filters);

        let employeeName = null,
            EmployeeNumber = null,
            DateJoined = null,
            Extension = null,
            RoleName = null;

        console.log("action=" + JSON.stringify(action))
        try {
            EmployeeNumber = action.filters.employeeName;
        } catch (error) {
        }
        console.log("employeeName=" + employeeName);
        try {
            EmployeeNumber = action.filters.EmployeeNumber;
        } catch (error) {
        }
        console.log("EmployeeNumber=" + EmployeeNumber);
        try {
            DateJoined = action.filters.DateJoined;
        } catch (error) {
        }
        console.log("DateJoined=" + DateJoined);
        try {
            Extension = action.filters.Extension;
        } catch (error) {
        }
        console.log("Extension=" + Extension);
        try {
            RoleName = action.filters.RoleName;
        } catch (error) {
        }
        console.log( !!whereStr)
        if (queryStr === '') {
            if(!!whereStr) {
                queryStr = '?where='+whereStr;
            } else {
                queryStr = ''
            }
            
        } else {
            queryStr = queryStr+'&where='+whereStr;
        }

        console.log('http://localhost:5000/api/v1/employees' + queryStr);
        const response = yield call(fetch, 'http://localhost:5000/api/v1/employees' + queryStr);
        //const data = response.json();
        const employees = yield call([response, 'json']);
        //console.log(JSON.stringify(employees));
        if (!!employees) {
            yield put({ type: actionTypes.FEATCH_EMPLOYEES_OK, employees: employees });
        }
        //return data[0].usernameName;
    } catch (e) {
        console.log(e);
        //return null;
    }
}

export function* fetchRoleAsync() {
    try {
        const response = yield call(fetch, 'http://localhost:5000/api/v1/roles');
        const roles = yield call([response, 'json']);
        if (!!roles) {
            yield put({ type: actionTypes.FEATCH_ROLES_OK, roles: roles });
        }
    } catch (e) {
        console.log(e);
    }
}

export function* watchInitLoad() {
    yield takeEvery('FETCH_EMPLOYEES_ASYNC', fetchEmployeeAsync);
    yield takeEvery('FETCH_EMPLOYEES_ASYNC', fetchRoleAsync);
}
