import React from 'react';
import store from '../store'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import actionTypes from '../actionTypes'
import { onUpdateEmployee } from '../actions'

class ChangeEmployee extends React.Component {
    componentDidMount() {
        let pathname = this.props.location.pathname
        let EmployeeID = pathname.substring(pathname.lastIndexOf('/') + 1);
        store.dispatch({ type: actionTypes.FETCH_EMPLOYEE_TOBE_UPDATE_ASYNC, EmployeeID: EmployeeID });
    }
    render() {
        let { employeeTobeChange, roles } = this.props;

        let roleList = '';
        console.log(employeeTobeChange);

        if (!!roles) {
            //console.log(JSON.stringify(roles))
            roleList = roles.map(role => (
                <option value={role.RoleName}>{role.RoleName}</option>
            ));
        }

        let detailItems = '';
        if (!!employeeTobeChange) {
            let defaultRoleName = '';
            try {
                defaultRoleName = employeeTobeChange.Role.RoleName;
            } catch (error) {
                console.log(error);
            }

            detailItems = (
                <table>
                    <tr>
                        <td>Employee ID: </td>
                        <td><text id="EmployeeID">{employeeTobeChange.EmployeeID}</text></td>
                    </tr>
                    <tr>
                        <td>Employee Number:</td>
                        <td><input id="EmployeeNumber" value={employeeTobeChange.EmployeeNumber}></input></td>
                    </tr>
                    <tr>
                        <td>First Name:</td>
                        <td><input id="FirstName" value={employeeTobeChange.FirstName}></input> </td>
                    </tr>
                    <tr>
                        <td>Last Name:</td>
                        <td><input id="LastName" value={employeeTobeChange.LastName}></input> </td>
                    </tr>
                    <tr>
                        <td>Extension:</td>
                        <td><input id="Extension" value={employeeTobeChange.Extension}></input></td>
                    </tr>
                    <tr>
                        <td>RoleName:</td>
                        <td>
                            <select id="EmployeeID" value = {defaultRoleID}>
                                <option value=''></option>
                                {roleList}
                            </select>
                        </td>
                    </tr>
                    <tr>
                        <td colSpan = "2">
                            <button onClick={() => onUpdateEmployee(
                                document.getElementById(id).value,
                                employeeTobeChange.EmployeeNumber,
                                employeeTobeChange.FirstName,
                                employeeTobeChange.LastName,
                                employeeTobeChange.Extension,
                                employeeTobeChange.RoleID
                            )}>Confirm</button>
                            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                            <button>Go back</button>
                        </td>
                    </tr>
                </table>);
        }
        return (
            <div id="employee-detail">
                {detailItems}
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    let { employeeTobeChange, roles } = state.dataReducer
    return {
        employeeTobeChange: employeeTobeChange, roles: roles
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onUpdateEmployee: bindActionCreators(onUpdateEmployee, dispatch),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ChangeEmployee)
