import React from 'react'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { onClickEmployee, onClickPageNo } from '../actions'
import actionTypes from '../actionTypes'
import store from '../store'
import { EmployeeLeft } from './employeeLeft'
import { EmployeeDetail } from './employeeDetail'
import querySearch from 'stringquery'



class Employee extends React.Component {
    componentDidMount() {
        store.dispatch({type: actionTypes.FETCH_EMPLOYEES_ASYNC, query: this.props.location.search});
    }
    render() {
        let { employees, pageInfo, selectedEmployee, onClickEmployee } = this.props;

        let queryParams = querySearch(this.props.location.search);

        return (
            <div>
                <div id='left'>
                    <EmployeeLeft employees={employees} pageInfo={pageInfo} onClickEmployee = {onClickEmployee}
                     queryParams = {queryParams} />
                </div>
                <div id='middle'>
                    <EmployeeDetail selectedEmployee={selectedEmployee} />
                </div>
                <div id='right'>
                    
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    let { employees, pageInfo, selectedEmployee } = state.dataReducer
    return {
        employees: employees, pageInfo: pageInfo, selectedEmployee: selectedEmployee
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClickEmployee: bindActionCreators(onClickEmployee, dispatch),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Employee)
